[y, fs] = wavread('sample1_birds.wav');t = linspace(0, length(y)/fs, length(y));figure; plot(t, y);xlabel("Time"); ylabel("Amplitude");title("Original Audio Signal in Time Domain");N = 1024;f = linspace(0, fs, N);X = abs(fft(y, N));figure; plot(f(1:N/2), X(1:N/2));xlabel("Frequency"); ylabel("Amplitude");title("Original Audio Signal in Frequency Domain");X(1:20) = 0;figure; plot(f(1:N/2), X(1:N/2));xlabel("Frequency"); ylabel("Amplitude");title("Modified Audio Signal in Frequency Domain");y_prime = ifft(X, N);t = linspace(0, length(y_prime)/fs, length(y_prime));figure; plot(t, y_prime);xlabel("Time"); ylabel("Amplitude");title("Modified Audio Signal in Time Domain");